mapper.update:
UPDATE cart SET cart_cnt = #{cartCnt}
WHERE cust_id = #{custId} AND item_key = #{itemKey}


          AND option_key = #{optionKey}


          AND option_key IS NULL

mapper.delete:
DELETE FROM cart
WHERE cust_id = #{custId} AND item_key = #{itemKey}


          AND option_key = #{optionKey}


          AND option_key IS NULL
